import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(`${process.env.REACT_APP_API_URL}/login/`, {
        username,
        password,
      });
      const { access, refresh } = response.data;

      localStorage.setItem('access', access);
      localStorage.setItem('refresh', refresh);

      // Get user info
      const userRes = await axios.get(`${process.env.REACT_APP_API_URL}/view-accounts/`, {
        headers: { Authorization: `Bearer ${access}` },
      });

      const role = userRes.data.role;

      if (role === 'admin') {
        navigate('/admin');
      } else if (role === 'customer') {
        navigate('/customer');
      } else {
        alert('Unknown role');
      }

    } catch (error) {
      alert('Invalid credentials');
    }
  };

  return (
    <div className="container mt-5">
      <h3 className="mb-4">Login</h3>
      <form onSubmit={handleLogin}>
        <input type="text" placeholder="Username" onChange={e => setUsername(e.target.value)} className="form-control mb-2" />
        <input type="password" placeholder="Password" onChange={e => setPassword(e.target.value)} className="form-control mb-2" />
        <button type="submit" className="btn btn-primary">Login</button>
      </form>
    </div>
  );
}

export default Login;
